import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import AuthForm from './components/AuthForm';
import Dashboard from './components/Dashboard';
import NoteDetail from './components/NoteDetail';

function App() {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [authError, setAuthError] = useState('');

  const API_BASE = 'http://localhost:5001/api';

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');

    if (token && savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setIsLoading(false);
  }, []);

  const handleAuth = async (formData, mode) => {
    setIsLoading(true);
    setAuthError('');

    try {
      const endpoint = mode === 'login' ? '/auth/login' : '/auth/signup';
      const response = await axios.post(`${API_BASE}${endpoint}`, {
        email: formData.email,
        password: formData.password
      });

      const { token, user: userData } = response.data;

      // Save to localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));

      // Set user state
      setUser(userData);

    } catch (error) {
      console.error('Auth error:', error);
      setAuthError(error.response?.data?.message || 'Authentication failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  // Fetch user info from backend
  const fetchUserInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_BASE}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data.user);
      localStorage.setItem('user', JSON.stringify(response.data.user));
    } catch (error) {
      // fallback: logout if error
      handleLogout();
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public routes */}
          <Route 
            path="/login" 
            element={
              user ? <Navigate to="/dashboard" replace /> : (
                <AuthForm 
                  mode="login" 
                  onSubmit={(formData) => handleAuth(formData, 'login')}
                  isLoading={isLoading}
                  error={authError}
                />
              )
            } 
          />
          <Route 
            path="/signup" 
            element={
              user ? <Navigate to="/dashboard" replace /> : (
                <AuthForm 
                  mode="signup" 
                  onSubmit={(formData) => handleAuth(formData, 'signup')}
                  isLoading={isLoading}
                  error={authError}
                />
              )
            } 
          />

          {/* Protected routes */}
          <Route 
            path="/dashboard" 
            element={
              user ? <Dashboard user={user} onLogout={handleLogout} onNoteUpload={fetchUserInfo} /> : <Navigate to="/login" replace />
            } 
          />
          <Route 
            path="/note/:id" 
            element={user ? <NoteDetail /> : <Navigate to="/login" replace />} 
          />

          {/* Landing page */}
          <Route 
            path="/" 
            element={
              user ? <Navigate to="/dashboard" replace /> : <LandingPage />
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

