import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const QUIZ_TYPES = [
  { value: 'flashcard', label: 'Flashcard' },
  { value: 'mcq', label: 'MCQ' },
  { value: 'fill-in-the-blank', label: 'Fill in the Blank' }
];

const Dashboard = ({ user, onLogout, onNoteUpload, themeToggle }) => {
  const [notes, setNotes] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadForm, setUploadForm] = useState({
    filename: '',
    content: '',
    tags: ''
  });
  const [showQuizModal, setShowQuizModal] = useState(false);
  const [quizNoteId, setQuizNoteId] = useState(null);
  const [quizType, setQuizType] = useState('flashcard');
  const [isGenerating, setIsGenerating] = useState(false);

  const API_BASE = 'http://localhost:5001/api';

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_BASE}/notes`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotes(response.data);
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!uploadForm.filename || !uploadForm.content) {
      alert('Please fill in all required fields');
      return;
    }

    setIsUploading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(`${API_BASE}/notes`, {
        filename: uploadForm.filename,
        content: uploadForm.content,
        tags: uploadForm.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setNotes([response.data.note, ...notes]);
      setUploadForm({ filename: '', content: '', tags: '' });
      alert(`Note uploaded successfully! +${response.data.xpGained} XP`);
      if (onNoteUpload) await onNoteUpload();
    } catch (error) {
      console.error('Error uploading note:', error);
      alert('Error uploading note');
    } finally {
      setIsUploading(false);
    }
  };

  const handleGenerateQuizClick = (noteId) => {
    setQuizNoteId(noteId);
    setQuizType('flashcard');
    setShowQuizModal(true);
  };

  const handleGenerateQuiz = async () => {
    setIsGenerating(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_BASE}/notes/${quizNoteId}/generate-quiz`, { type: quizType }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setShowQuizModal(false);
      setQuizNoteId(null);
      setQuizType('flashcard');
      fetchNotes();
      alert('Quiz generated successfully!');
    } catch (error) {
      alert('Quiz generation failed. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout();
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark text-dark-900 dark:text-white pb-12 transition-colors">
      <nav className="bg-white dark:bg-dark-900 border-b border-gray-200 dark:border-dark-800 shadow-lg transition-colors">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16 items-center">
            <div className="flex items-center">
              <Link to="/" className="flex items-center group">
                {/* SVG Brain Icon */}
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 32 32" width="32" height="32" className="mr-2" fill="currentColor" style={{ color: '#a259ff' }}>
                  <path d="M16 2c-2.21 0-4 1.79-4 4v1.09C8.61 7.56 6 10.47 6 14v1c0 1.1.9 2 2 2v1c0 1.1.9 2 2 2v1c0 1.1.9 2 2 2s2-.9 2-2v-1c1.1 0 2-.9 2-2v-1c1.1 0 2-.9 2-2v-1c1.1 0 2-.9 2-2v-1c0-3.53-2.61-6.44-6-6.91V6c0-2.21-1.79-4-4-4zm0 2c1.1 0 2 .9 2 2v1.09c2.39.47 4 2.38 4 4.91v1c0 .55-.45 1-1 1s-1-.45-1-1v-1c0-1.1-.9-2-2-2s-2 .9-2 2v1c0 .55-.45 1-1 1s-1-.45-1-1v-1c0-2.53 1.61-4.44 4-4.91V6c0-1.1-.9-2-2-2z"/>
                </svg>
                <span className="text-2xl font-bold text-accent group-hover:text-accent-blue transition">NeuroRecall</span>
              </Link>
            </div>
            <div className="flex items-center space-x-6">
              <div className="text-sm text-gray-700 dark:text-gray-300 text-right">
                <span className="font-semibold text-dark-900 dark:text-white block">{user.email}</span>
                <div className="flex items-center space-x-3 mt-1">
                  <span className="text-accent">XP: {user.xp}</span>
                  <span className="text-accent-blue">Rank: {user.rank}</span>
                  <span className="text-pink-400">Streak: {user.streak}</span>
                </div>
              </div>
              {themeToggle}
              <button
                onClick={handleLogout}
                className="bg-red-600 hover:bg-red-700 text-white px-5 py-2 rounded-lg text-sm font-semibold shadow-glow transition"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-3xl mx-auto py-10 px-4">
        {/* Upload Section */}
        <div className="bg-white dark:bg-dark-800 border border-gray-200 dark:border-dark-900 rounded-2xl shadow-glow mb-10 p-8 transition-colors">
          <h3 className="text-2xl font-bold text-dark-900 dark:text-white mb-6">Upload New Note</h3>
          <form onSubmit={handleUpload} className="space-y-6">
            <div>
              <label htmlFor="filename" className="block text-sm font-medium text-gray-300 mb-1">
                Note Title
              </label>
              <input
                type="text"
                id="filename"
                value={uploadForm.filename}
                onChange={(e) => setUploadForm({...uploadForm, filename: e.target.value})}
                className="w-full px-4 py-3 bg-white dark:bg-dark-900 border border-gray-300 dark:border-dark-700 text-dark-900 dark:text-white rounded-lg focus:ring-accent focus:border-accent placeholder-gray-500 transition"
                placeholder="Enter note title"
                required
              />
            </div>
            <div>
              <label htmlFor="content" className="block text-sm font-medium text-gray-300 mb-1">
                Content
              </label>
              <textarea
                id="content"
                rows={6}
                value={uploadForm.content}
                onChange={(e) => setUploadForm({...uploadForm, content: e.target.value})}
                className="w-full px-4 py-3 bg-white dark:bg-dark-900 border border-gray-300 dark:border-dark-700 text-dark-900 dark:text-white rounded-lg focus:ring-accent focus:border-accent placeholder-gray-500 transition"
                placeholder="Paste your notes here or write them directly..."
                required
              />
            </div>
            <div>
              <label htmlFor="tags" className="block text-sm font-medium text-gray-300 mb-1">
                Tags (comma-separated)
              </label>
              <input
                type="text"
                id="tags"
                value={uploadForm.tags}
                onChange={(e) => setUploadForm({...uploadForm, tags: e.target.value})}
                className="w-full px-4 py-3 bg-white dark:bg-dark-900 border border-gray-300 dark:border-dark-700 text-dark-900 dark:text-white rounded-lg focus:ring-accent focus:border-accent placeholder-gray-500 transition"
                placeholder="e.g., math, physics, biology"
              />
            </div>
            <button
              type="submit"
              disabled={isUploading}
              className="w-full py-3 rounded-lg font-semibold bg-accent hover:bg-accent-blue shadow-glow text-white text-lg transition disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isUploading ? 'Uploading...' : 'Upload Note'}
            </button>
          </form>
        </div>

        {/* Notes List */}
        <div className="bg-white dark:bg-dark-800 border border-gray-200 dark:border-dark-900 rounded-2xl shadow-glow transition-colors">
          <div className="px-8 py-6 border-b border-gray-200 dark:border-dark-900">
            <h3 className="text-xl font-bold text-dark-900 dark:text-white">
              Your Notes ({notes.length})
            </h3>
          </div>
          <ul className="divide-y divide-gray-200 dark:divide-dark-900">
            {notes.map((note) => (
              <li key={note._id} className="px-8 py-6 transition hover:bg-gray-100 dark:hover:bg-dark-900">
                <div className="flex items-center justify-between">
                  <div className="flex-1 min-w-0">
                    <h4 className="text-lg font-semibold text-accent truncate">
                      <Link to={`/note/${note._id}`} className="hover:underline">
                        {note.filename}
                      </Link>
                    </h4>
                    <p className="mt-1 text-base text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-dark-900 rounded-lg px-3 py-2">
                      {note.content.substring(0, 100)}...
                    </p>
                    <div className="mt-2 flex items-center text-xs text-gray-500">
                      <span>Uploaded: {new Date(note.uploadDate).toLocaleDateString()}</span>
                      {note.tags.length > 0 && (
                        <span className="ml-4 text-accent-blue">
                          Tags: {note.tags.join(', ')}
                        </span>
                      )}
                    </div>
                  </div>
                  <div className="ml-4 flex-shrink-0">
                    <button className="bg-accent hover:bg-accent-blue text-white px-4 py-2 rounded-lg text-sm font-semibold shadow-glow transition"
                      onClick={() => handleGenerateQuizClick(note._id)}
                    >
                      Generate Quiz
                    </button>
                  </div>
                </div>
              </li>
            ))}
            {notes.length === 0 && (
              <li className="px-8 py-12 text-center text-gray-500">
                No notes uploaded yet. Upload your first note to get started!
              </li>
            )}
          </ul>
        </div>
        {/* Quiz Type Modal */}
        {showQuizModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
            <div className="bg-white dark:bg-dark-800 rounded-2xl shadow-xl p-8 w-full max-w-sm border border-gray-200 dark:border-dark-900">
              <h3 className="text-xl font-bold mb-4 text-dark-900 dark:text-white">Select Quiz Type</h3>
              <select
                className="w-full mb-6 px-4 py-2 rounded-lg border border-gray-300 dark:border-dark-700 bg-gray-100 dark:bg-dark-900 text-dark-900 dark:text-white focus:ring-accent focus:border-accent"
                value={quizType}
                onChange={e => setQuizType(e.target.value)}
                disabled={isGenerating}
              >
                {QUIZ_TYPES.map(q => (
                  <option key={q.value} value={q.value}>{q.label}</option>
                ))}
              </select>
              <div className="flex justify-end space-x-2">
                <button
                  className="px-4 py-2 rounded-lg bg-gray-200 dark:bg-dark-700 text-dark-900 dark:text-white font-semibold hover:bg-gray-300 dark:hover:bg-dark-800 transition"
                  onClick={() => setShowQuizModal(false)}
                  disabled={isGenerating}
                >
                  Cancel
                </button>
                <button
                  className="px-4 py-2 rounded-lg bg-accent hover:bg-accent-blue text-white font-semibold shadow-glow transition disabled:opacity-50"
                  onClick={handleGenerateQuiz}
                  disabled={isGenerating}
                >
                  {isGenerating ? 'Generating...' : 'Generate'}
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
