import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import AuthForm from './components/AuthForm';
import Dashboard from './components/Dashboard';
import NoteDetail from './components/NoteDetail';

// Theme toggle button
function ThemeToggle({ theme, setTheme }) {
  return (
    <button
      aria-label="Toggle theme"
      onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
      className="ml-4 p-2 rounded-full bg-dark-800 dark:bg-gray-200 hover:bg-accent hover:dark:bg-accent transition shadow-glow"
    >
      {theme === 'dark' ? (
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-yellow-300" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m8.66-13.66l-.71.71M4.05 19.07l-.71.71M21 12h-1M4 12H3m16.66 5.66l-.71-.71M4.05 4.93l-.71-.71M16 12a4 4 0 11-8 0 4 4 0 018 0z" /></svg>
      ) : (
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12.79A9 9 0 1111.21 3a7 7 0 109.79 9.79z" /></svg>
      )}
    </button>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [authError, setAuthError] = useState('');
  const [theme, setTheme] = useState('dark');

  const API_BASE = 'http://localhost:5001/api';

  // Theme effect
  useEffect(() => {
    // Load theme from localStorage or system
    const saved = localStorage.getItem('theme');
    if (saved) setTheme(saved);
    else if (window.matchMedia('(prefers-color-scheme: dark)').matches) setTheme('dark');
    else setTheme('light');
  }, []);
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');

    if (token && savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setIsLoading(false);
  }, []);

  const handleAuth = async (formData, mode) => {
    setIsLoading(true);
    setAuthError('');

    try {
      const endpoint = mode === 'login' ? '/auth/login' : '/auth/signup';
      const response = await axios.post(`${API_BASE}${endpoint}`, {
        email: formData.email,
        password: formData.password
      });

      const { token, user: userData } = response.data;

      // Save to localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));

      // Set user state
      setUser(userData);

    } catch (error) {
      console.error('Auth error:', error);
      setAuthError(error.response?.data?.message || 'Authentication failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  // Fetch user info from backend
  const fetchUserInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_BASE}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data.user);
      localStorage.setItem('user', JSON.stringify(response.data.user));
    } catch (error) {
      // fallback: logout if error
      handleLogout();
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-dark">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-accent mx-auto"></div>
          <p className="mt-4 text-gray-300">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public routes */}
          <Route 
            path="/login" 
            element={
              user ? <Navigate to="/dashboard" replace /> : (
                <AuthForm 
                  mode="login" 
                  onSubmit={(formData) => handleAuth(formData, 'login')}
                  isLoading={isLoading}
                  error={authError}
                />
              )
            } 
          />
          <Route 
            path="/signup" 
            element={
              user ? <Navigate to="/dashboard" replace /> : (
                <AuthForm 
                  mode="signup" 
                  onSubmit={(formData) => handleAuth(formData, 'signup')}
                  isLoading={isLoading}
                  error={authError}
                />
              )
            } 
          />

          {/* Protected routes */}
          <Route 
            path="/dashboard" 
            element={
              user ? <Dashboard user={user} onLogout={handleLogout} onNoteUpload={fetchUserInfo} themeToggle={<ThemeToggle theme={theme} setTheme={setTheme} />} /> : <Navigate to="/login" replace />
            } 
          />
          <Route 
            path="/note/:id" 
            element={user ? <NoteDetail /> : <Navigate to="/login" replace />} 
          />

          {/* Landing page */}
          <Route 
            path="/" 
            element={
              user ? <Navigate to="/dashboard" replace /> : <LandingPage themeToggle={<ThemeToggle theme={theme} setTheme={setTheme} />} />
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

// Landing page component
function LandingPage({ themeToggle }) {
  return (
    <div className="min-h-screen bg-dark text-white dark:bg-dark text-white" style={{backgroundImage: 'radial-gradient(ellipse at 50% 30%, #23263a 0%, #10131a 100%)'}}>
      {/* Navigation */}
      <nav className="bg-dark-900 shadow-lg border-b border-dark-800 flex items-center">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full flex justify-between h-16 items-center">
          <div className="flex items-center">
            <div className="flex-shrink-0 flex items-center">
              <span className="text-2xl font-bold text-accent flex items-center">
                <span className="mr-2">🧠</span> NeuroRecall
              </span>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            {themeToggle}
            <a
              href="/login"
              className="text-gray-300 hover:text-accent px-3 py-2 rounded-md text-sm font-medium transition"
            >
              Login
            </a>
            <a
              href="/signup"
              className="bg-accent hover:bg-accent-blue text-white px-4 py-2 rounded-lg text-sm font-semibold shadow-glow transition"
            >
              Sign Up
            </a>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="relative overflow-hidden">
        <div className="max-w-7xl mx-auto">
          <div className="relative z-10 pb-8 sm:pb-16 md:pb-20 lg:max-w-2xl lg:w-full lg:pb-28 xl:pb-32">
            <main className="mt-10 mx-auto max-w-7xl px-4 sm:mt-12 sm:px-6 md:mt-16 lg:mt-20 lg:px-8 xl:mt-28">
              <div className="sm:text-center lg:text-left">
                <h1 className="text-4xl tracking-tight font-extrabold text-white sm:text-5xl md:text-6xl">
                  <span className="block xl:inline">Master Your Learning with</span>{' '}
                  <span className="block text-accent xl:inline">AI-Powered Recall</span>
                </h1>
                <p className="mt-3 text-base text-gray-300 sm:mt-5 sm:text-lg sm:max-w-xl sm:mx-auto md:mt-5 md:text-xl lg:mx-0">
                  Upload your notes, generate intelligent quizzes, and optimize your learning with neuroscience-backed spaced repetition. 
                  Track your progress and unlock your full cognitive potential.
                </p>
                <div className="mt-5 sm:mt-8 sm:flex sm:justify-center lg:justify-start">
                  <div className="rounded-md shadow-glow">
                    <a
                      href="/signup"
                      className="w-full flex items-center justify-center px-8 py-3 border border-transparent text-base font-semibold rounded-lg text-white bg-accent hover:bg-accent-blue hover:text-white md:py-4 md:text-lg md:px-10 shadow-glow transition font-sans font-extrabold tracking-tight"
                    >
                      Get Started
                    </a>
                  </div>
                  <div className="mt-3 sm:mt-0 sm:ml-3">
                    <a
                      href="/login"
                      className="w-full flex items-center justify-center px-8 py-3 border border-transparent text-base font-semibold rounded-lg text-accent bg-dark-800 hover:bg-dark-900 hover:text-accent md:py-4 md:text-lg md:px-10 transition font-sans font-semibold"
                    >
                      Learn More
                    </a>
                  </div>
                </div>
              </div>
            </main>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="py-12 bg-dark-900 border-t border-dark-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="lg:text-center">
            <h2 className="text-base text-accent font-semibold tracking-wide uppercase">Features</h2>
            <p className="mt-2 text-3xl leading-8 font-extrabold tracking-tight text-white sm:text-4xl">
              Everything you need to optimize learning
            </p>
          </div>

          <div className="mt-10">
            <div className="space-y-10 md:space-y-0 md:grid md:grid-cols-2 md:gap-x-8 md:gap-y-10">
              <div className="relative">
                <div className="absolute flex items-center justify-center h-12 w-12 rounded-md bg-accent text-white shadow-glow">
                  📚
                </div>
                <p className="ml-16 text-lg leading-6 font-medium text-white">Smart Note Upload</p>
                <p className="mt-2 ml-16 text-base text-gray-300">
                  Upload PDFs, text files, or write notes directly. Our AI processes and organizes your content intelligently.
                </p>
              </div>

              <div className="relative">
                <div className="absolute flex items-center justify-center h-12 w-12 rounded-md bg-accent text-white shadow-glow">
                  🧠
                </div>
                <p className="ml-16 text-lg leading-6 font-medium text-white">AI Quiz Generation</p>
                <p className="mt-2 ml-16 text-base text-gray-300">
                  Generate flashcards, multiple choice questions, and fill-in-the-blanks from your notes automatically.
                </p>
              </div>

              <div className="relative">
                <div className="absolute flex items-center justify-center h-12 w-12 rounded-md bg-accent text-white shadow-glow">
                  ⏰
                </div>
                <p className="ml-16 text-lg leading-6 font-medium text-white">Spaced Repetition</p>
                <p className="mt-2 ml-16 text-base text-gray-300">
                  Based on neuroscience research, our algorithm optimizes review timing for maximum retention.
                </p>
              </div>

              <div className="relative">
                <div className="absolute flex items-center justify-center h-12 w-12 rounded-md bg-accent text-white shadow-glow">
                  📊
                </div>
                <p className="ml-16 text-lg leading-6 font-medium text-white">Progress Analytics</p>
                <p className="mt-2 ml-16 text-base text-gray-300">
                  Track your learning progress with detailed analytics and cognitive heatmaps.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
