import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = ({ user, onLogout, onNoteUpload }) => {
  const [notes, setNotes] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadForm, setUploadForm] = useState({
    filename: '',
    content: '',
    tags: ''
  });

  const API_BASE = 'http://localhost:5001/api';

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_BASE}/notes`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotes(response.data);
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!uploadForm.filename || !uploadForm.content) {
      alert('Please fill in all required fields');
      return;
    }

    setIsUploading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(`${API_BASE}/notes`, {
        filename: uploadForm.filename,
        content: uploadForm.content,
        tags: uploadForm.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setNotes([response.data.note, ...notes]);
      setUploadForm({ filename: '', content: '', tags: '' });
      alert(`Note uploaded successfully! +${response.data.xpGained} XP`);
      if (onNoteUpload) await onNoteUpload();
    } catch (error) {
      console.error('Error uploading note:', error);
      alert('Error uploading note');
    } finally {
      setIsUploading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout();
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-indigo-600">🧠 NeuroRecall</h1>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-sm text-gray-700">
                <span className="font-medium">{user.email}</span>
                <div className="flex items-center space-x-2">
                  <span className="text-indigo-600">XP: {user.xp}</span>
                  <span className="text-green-600">Rank: {user.rank}</span>
                  <span className="text-orange-600">Streak: {user.streak} 🔥</span>
                </div>
              </div>
              <button
                onClick={handleLogout}
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md text-sm font-medium"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="bg-white overflow-hidden shadow rounded-lg mb-6">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
              Upload New Note
            </h3>
            <form onSubmit={handleUpload} className="space-y-4">
              <div>
                <label htmlFor="filename" className="block text-sm font-medium text-gray-700">
                  Note Title
                </label>
                <input
                  type="text"
                  id="filename"
                  value={uploadForm.filename}
                  onChange={(e) => setUploadForm({...uploadForm, filename: e.target.value})}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                  placeholder="Enter note title"
                  required
                />
              </div>

              <div>
                <label htmlFor="content" className="block text-sm font-medium text-gray-700">
                  Content
                </label>
                <textarea
                  id="content"
                  rows={6}
                  value={uploadForm.content}
                  onChange={(e) => setUploadForm({...uploadForm, content: e.target.value})}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                  placeholder="Paste your notes here or write them directly..."
                  required
                />
              </div>

              <div>
                <label htmlFor="tags" className="block text-sm font-medium text-gray-700">
                  Tags (comma-separated)
                </label>
                <input
                  type="text"
                  id="tags"
                  value={uploadForm.tags}
                  onChange={(e) => setUploadForm({...uploadForm, tags: e.target.value})}
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                  placeholder="e.g., math, physics, biology"
                />
              </div>

              <button
                type="submit"
                disabled={isUploading}
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
              >
                {isUploading ? 'Uploading...' : 'Upload Note'}
              </button>
            </form>
          </div>
        </div>

        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">
              Your Notes ({notes.length})
            </h3>
          </div>
          <ul className="divide-y divide-gray-200">
            {notes.map((note) => (
              <li key={note._id} className="px-4 py-4 sm:px-6">
                <div className="flex items-center justify-between">
                  <div className="flex-1 min-w-0">
                    <h4 className="text-sm font-medium text-indigo-600 truncate">
                      <Link to={`/note/${note._id}`} className="hover:underline">
                        {note.filename}
                      </Link>
                    </h4>
                    <p className="mt-1 text-sm text-gray-500">
                      {note.content.substring(0, 100)}...
                    </p>
                    <div className="mt-2 flex items-center text-xs text-gray-500">
                      <span>Uploaded: {new Date(note.uploadDate).toLocaleDateString()}</span>
                      {note.tags.length > 0 && (
                        <span className="ml-4">
                          Tags: {note.tags.join(', ')}
                        </span>
                      )}
                    </div>
                  </div>
                  <div className="ml-4 flex-shrink-0">
                    <button className="bg-indigo-600 hover:bg-indigo-700 text-white px-3 py-1 rounded text-sm">
                      Generate Quiz
                    </button>
                  </div>
                </div>
              </li>
            ))}
            {notes.length === 0 && (
              <li className="px-4 py-8 sm:px-6 text-center text-gray-500">
                No notes uploaded yet. Upload your first note to get started!
              </li>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
