import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate, Link } from 'react-router-dom';

const API_BASE = 'http://localhost:5001/api';

const NoteDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [note, setNote] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`${API_BASE}/notes/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setNote(response.data);
      } catch (err) {
        setError('Could not fetch note.');
      } finally {
        setLoading(false);
      }
    };
    fetchNote();
  }, [id]);

  if (loading) return <div className="p-8 text-center text-gray-300">Loading...</div>;
  if (error) return <div className="p-8 text-center text-red-400">{error}</div>;
  if (!note) return null;

  return (
    <div className="min-h-screen bg-dark text-white">
      {/* Top Bar */}
      <nav className="flex items-center justify-between px-8 py-6 bg-dark-900 border-b border-dark-800 shadow-glow">
        <Link to="/" className="flex items-center space-x-2 group">
          <span className="text-2xl font-bold text-accent group-hover:text-accent-blue transition">üß†</span>
          <span className="text-xl font-extrabold text-white group-hover:text-accent-blue transition tracking-tight font-sans">NeuroRecall</span>
        </Link>
      </nav>
      {/* Back Button */}
      <div className="max-w-3xl mx-auto px-4 mt-10 flex items-center">
        <button
          className="bg-accent hover:bg-accent-blue text-white font-semibold font-sans rounded-full px-6 py-2 shadow-glow text-lg flex items-center transition focus:outline-none focus:ring-2 focus:ring-accent"
          onClick={() => navigate(-1)}
        >
          <span className="mr-2 text-xl">‚Üê</span> Back
        </button>
      </div>
      {/* Note Card */}
      <div className="max-w-3xl mx-auto px-4 mt-6">
        <div className="bg-dark-800 border border-dark-900 shadow-glow rounded-2xl p-8">
          <h2 className="text-3xl font-extrabold text-accent mb-2 font-sans">{note.filename}</h2>
          <div className="mb-2 text-sm text-gray-400 font-sans">
            Uploaded: {new Date(note.uploadDate).toLocaleDateString()}
          </div>
          {note.tags.length > 0 && (
            <div className="mb-2 text-xs text-accent-blue font-sans">
              Tags: {note.tags.join(', ')}
            </div>
          )}
          <pre className="whitespace-pre-wrap text-gray-100 bg-dark-900 p-6 rounded-xl mt-4 overflow-x-auto text-base font-mono shadow-inner border border-dark-800">
            {note.content}
          </pre>
        </div>
      </div>
    </div>
  );
};

export default NoteDetail;
