import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const API_BASE = 'http://localhost:5001/api';

const NoteDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [note, setNote] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`${API_BASE}/notes/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setNote(response.data);
      } catch (err) {
        setError('Could not fetch note.');
      } finally {
        setLoading(false);
      }
    };
    fetchNote();
  }, [id]);

  if (loading) return <div className="p-8 text-center text-gray-300">Loading...</div>;
  if (error) return <div className="p-8 text-center text-red-400">{error}</div>;
  if (!note) return null;

  return (
    <div className="max-w-3xl mx-auto py-10 px-4">
      <button
        className="mb-6 text-accent hover:underline text-lg font-semibold"
        onClick={() => navigate(-1)}
      >
        ‚Üê Back
      </button>
      <div className="bg-dark-800 border border-dark-900 shadow-glow rounded-2xl p-8">
        <h2 className="text-3xl font-bold text-accent mb-2">{note.filename}</h2>
        <div className="mb-2 text-sm text-gray-400">
          Uploaded: {new Date(note.uploadDate).toLocaleDateString()}
        </div>
        {note.tags.length > 0 && (
          <div className="mb-2 text-xs text-accent-blue">
            Tags: {note.tags.join(', ')}
          </div>
        )}
        <pre className="whitespace-pre-wrap text-gray-100 bg-dark-900 p-6 rounded-xl mt-4 overflow-x-auto text-base font-mono shadow-inner border border-dark-800">
          {note.content}
        </pre>
      </div>
    </div>
  );
};

export default NoteDetail;
